    private void tweetKaydetActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_tweetKaydetActionPerformed
        // TODO add your handling code here:
        try {
            File dosya = new File("tweetBilgileri.txt");
            FileWriter yazici = new FileWriter(dosya, true);
            BufferedWriter yaz = new BufferedWriter(yazici);
            yaz.write(kategori.getSelectedItem().toString() + "," + hashtag.getText() + "," + tweet.getText() + "." + "\n");
            yaz.newLine();
            yaz.close();

        } catch (Exception hata) {
            hata.printStackTrace();
        }

    }//GEN-LAST:event_tweetKaydetActionPerformed


